import React, { Component } from 'react';
import poundingHeartAnimation from '../../assets/poundingHeart.svg';
import FullHeart from '../../assets/fullHeart.svg';
import poppingHeartGIF from '../../assets/poppingHeartGIF.svg';

import Vacation from '../../models/Vacation';
import { store } from '../../Redux/Store';
import './favoriteVacations.css';



interface vacationsState {
    favoriteVacations : Vacation[];
}


export default class FavoriteVacations extends Component <any, vacationsState> {

    constructor(props : any) {
        super(props);

        this.state = {
            favoriteVacations : new Array <Vacation> ()
        }
    }

    componentDidMount = () => {
        
        this.setState({
            favoriteVacations: store.getState().favoriteVacations
        })
    }
    

    render() {
        return (
            <div className="favoriteVacationsSection">

                {/* ---------- Displayed Vacations Section ---------- */}

                <div className="vacationsDisplaySection">

                    {/* ---------- Favorite Vacations Section ---------- */}

                    <div className="favoriteVacationsSection">
                        <h3 className="favoritesHeader sharp-text">Your {this.state.favoriteVacations.length} Favorite Vacations
                            <img className="poundingHeartAnimation" src={poppingHeartGIF} alt="Followed GIF"/>
                        </h3>


                        <div className="favoriteVacations">
                            {/* ---------- Favorite Vacations Cards ---------- */}

                            {this.state.favoriteVacations.map( (vacation : Vacation, index : number) => 
                                <div className="vacationCardContainer" key={index}>
                                    <img className="vacationCardImg" src={vacation.Image_URL} alt={vacation.Vacation_Description}/>
    
                                    <div className="vacationCard">
                                        <div className="topVacationCardSection">
                                            <img className="heartIcon" src={FullHeart} alt="Like Button"/>
                                            <p className="followersText">{vacation.Followers_Count}</p>
                                        </div>
    
                                        <div className="vacationInfoCard">
                                            <h4 className="resortName">{vacation.Vacation_Description}</h4>
    
                                            <div className="vacationDates">
                                                <h4 className="vacationDate">{vacation.Start_Date}</h4>
                                                <span> - </span>
                                                <h4 className="vacationDate">{vacation.End_Date}</h4>
                                            </div>
    
                                            <h4 className="vacationPrice">{vacation.Vacation_Price} $</h4>
                                        </div>
                                    </div>
                                    
                                </div>
                            )}
                        </div>
                    </div>

                </div>

            </div>
        )
    }
}
