// -------------------- This Model Retrieves The User's Type From The sessionStorage And Returns It -------------------- //

// Explanation -> This Model is just the FIRST layer of valdiation in the clientside. This Model does not determine whether the user is an admin or not.
// The real valdiation happens on the server, with the user's cache that is saved in the server.
// This client validation is the first layer of 2 layers, that are meant to speed up proccess and for a better UX -> The goal is not to send any
// request to the server if it is not absolutely necessary.


import axios from 'axios';

export const getUserTypeFromServer :any = async () => {

    try {

        const serverResponse = await axios.post(`http://localhost:3001/users/type$`);
        const userTypeFromServer = serverResponse.data;

        return userTypeFromServer;
    }

    catch (error) {
        alert(error);
        return;
    }

