    this.vacationsErrorHandler = errorMessage => {
      // Letting the user know he received an error, by displaying a Pop-Up Message at the bottom of the screen
      let snackbar = document.getElementById("snackbar");
      snackbar.className = "show";
      snackbar.textContent = errorMessage;
      setTimeout(function () {
        snackbar.className = snackbar.className.replace("show", "");
      }, 6000);
    };

    this.displayVacationNameInEditModal = clickedVacationName => {
      // Displaying 'Edit' Modal Name
      const editedVacationNameField = document.getElementById("editedVacationName");
      editedVacationNameField.value = clickedVacationName;
    };

    this.displayVacationPriceInEditModal = clickedVacationPrice => {
      // Dusplaying 'Edit' Modal Price
      const editedVacationPriceField = document.getElementById("editedVacationPrice");
      editedVacationPriceField.valueAsNumber = clickedVacationPrice;
    };

    this.displayVacationDatesInEditModal = (clickedVacationStartDate, clickedVacationEndDate) => {
      // Displaying 'Edit' Modal Dates
      const editStartDateField = document.getElementById("editStartDate");
      const editEndDateField = document.getElementById("editEndDate"); // Start Date

      let startDateYear = +clickedVacationStartDate.split('/')[2];
      let startDateMonth = +clickedVacationStartDate.split('/')[1];
      let startDateDay = +clickedVacationStartDate.split('/')[0];
      let fullStartDate = startDateYear + "-" + startDateMonth + "-" + startDateDay; // End Date

      let endDateYear = +clickedVacationEndDate.split('/')[2];
      let endDateMonth = +clickedVacationEndDate.split('/')[1];
      let endDateDay = +clickedVacationEndDate.split('/')[0];
      let fullEndDate = endDateYear + "-" + endDateMonth + "-" + endDateDay;
      editStartDateField.valueAsDate = new Date(fullStartDate);
      editEndDateField.valueAsDate = new Date(fullEndDate);
    };

    this.displayVacationDescriptionInEditModal = clickedVacationDescription => {
      // Displaying 'Edit' Modal Description
      const editVacationDescriptionField = document.getElementById("editedVacationDescription");
      editVacationDescriptionField.value = clickedVacationDescription;
    };

    this.displayVacationImageInEditModal = clickedVacationImageURL => {
      const editedVacationImageURL = document.getElementById("editedVacationImageURL");
      editedVacationImageURL.value = clickedVacationImageURL;
    };

    this.displayAdditionModal = () => {
      // Defining the 'Add' Modal in the UI
      const addModalBackground = document.getElementById("addModalContainer");
      const addModalDoneBtn = document.getElementById("addModalDoneBtn");
      const addModalCancelBtn = document.getElementById("addModalCancelBtn");
      addModalBackground.style.display = "block"; // Defining the event listeners for the modal

      addModalCancelBtn.onclick = () => {
        this.hideModal(addModalBackground);
      };

      addModalDoneBtn.onclick = () => {
        this.addVacation(addModalBackground);
      };

      window.addEventListener("click", event => {
        if (event.target === addModalBackground) {
          this.hideModal(addModalBackground);
        }
      });
    };

    this.displayEditModal = clickedVacation => {
      // Defining the 'Edit' Modal in the UI
      const editModalBackground = document.getElementById("editModalContainer");
      const editModalIcon = document.getElementById("editModalIcon");
      const editModalHeader = document.getElementById("editModalHeader");
      const editModalDoneBtn = document.getElementById("editModalDoneBtn");
      const editModalCancelBtn = document.getElementById("editModalCancelBtn"); // Displaying the values inside the 'Edit' Modal

      this.displayVacationNameInEditModal(clickedVacation.vacationName);
      this.displayVacationPriceInEditModal(clickedVacation.vacationPrice);
      this.displayVacationDescriptionInEditModal(clickedVacation.vacationDescription);
      this.displayVacationDatesInEditModal(clickedVacation.startDate, clickedVacation.endDate);
      this.displayVacationImageInEditModal(clickedVacation.imageURL);
      editModalBackground.style.display = "block";
      editModalHeader.textContent = `Update ${clickedVacation.vacationName}`;
      editModalIcon.src = clickedVacation.imageURL; // Defining the event listeners for the modal

      editModalCancelBtn.onclick = () => {
        this.hideModal(editModalBackground);
      };

      editModalDoneBtn.onclick = () => {
        this.updateVacation(clickedVacation, editModalBackground);
      };

      window.onclick = function (event) {
        if (event.target === editModalBackground) {
          editModalBackground.style.display = "none";
        }
      };
    };

    this.displayDeletionModal = clickedVacation => {
      let clickedVacationID = clickedVacation.vacationID;
      const deletionModalBackground = document.getElementById("deletionModalContainer");
      const deletionModalHeader = document.getElementById("deletionModalHeader");
      const deletionModalTextParagraph = document.getElementById("deletionModalText");
      const deleteBtn = document.getElementById("deleteBtn");
      const dontDeleteBtn = document.getElementById("dontDeleteBtn");
      const deletionModalVacationName = document.getElementById("deletionModalVacationName");
      deletionModalBackground.style.display = "block";
      deletionModalHeader.textContent = "Confirm Your Deletion";
      deletionModalTextParagraph.textContent = `Are You Sure You Would Like To Delete`;
      deletionModalVacationName.textContent = `${clickedVacation.vacationName} ?`; // Defining the event listeners for the modal

      dontDeleteBtn.onclick = () => {
        this.hideModal(deletionModalBackground);
      };

      deleteBtn.onclick = () => {
        this.deleteVacation(clickedVacationID);
        this.hideModal(deletionModalBackground);
      };

      window.onclick = function (event) {
        if (event.target === deletionModalBackground) {
          deletionModalBackground.style.display = "none";
        }
      };
    };

    this.clearModalInputs = () => {
      // Clearing the inputs of the 'Add' Modal
      let additionModalNameInput = document.getElementById("addedVacationName");
      let additionModalStartDateInput = document.getElementById("addStartDate");
      let additionModalEndDateInput = document.getElementById("addEndDate");
      let additionModalPriceInput = document.getElementById("addedVacationPrice");
      let additionModalDescriptionInput = document.getElementById("addedVacationDescription");
      let additionModalImageURLInput = document.getElementById("addedVacationImageURL");
      additionModalNameInput.value = "";
      additionModalStartDateInput.value = "";
      additionModalEndDateInput.value = "";
      additionModalPriceInput.valueAsNumber = 0;
      additionModalDescriptionInput.value = "";
      additionModalImageURLInput.value = "";
    };

    this.hideModal = modal => {
      modal.style.display = "none";
      this.clearModalInputs();
    };

    this.state = {
      allVacations: new Array()
    };
