import React, { ChangeEvent, Component } from 'react';
import logo from '../../assets/welcomeSVG.png';
import UserLoginDetails from '../../models/UserLoginDetails';
import SuccessfulLoginServerResponse from '../../models/SuccessfulLoginServerResponse';
import { NavLink } from 'react-router-dom';
import axios from 'axios';
import './loginPage.css';
import Vacation from '../../models/Vacation';
import { store } from '../../Redux/Store';
import { ActionType } from '../../Redux/ActionType';


interface LoginState {
    userName: string;
    password: string;
}

export default class LoginPage extends Component <any, LoginState> {

    // Defining the input and password fields globally
    private usernameInputField: HTMLFieldSetElement;
    private passwordInputField: HTMLFieldSetElement;
    private userMessagesParagraph: HTMLParagraphElement;

    public constructor (props: any) {
        super(props);

        // initializing the state with empty values
        this.state = {
             userName: "",
             password: ""
        };
    }

    componentDidMount = () => {

        // Retrieving all vacations from the DB before the user even logs in, in order to load
        // the data behind the scenes, while the user is bussy logging
        this.getAllVacationsFromServer();

        // Assigning the global elements inside the DOM
        this.usernameInputField = document.getElementById("usernameFieldset") as HTMLFieldSetElement;
        this.passwordInputField = document.getElementById("passwordFieldset") as HTMLFieldSetElement;

        this.userMessagesParagraph = document.getElementById("userMessagesParagraph") as HTMLParagraphElement;
    }


    private getAllVacationsFromServer = async () => {
        
        try {
            // The response we get, should be type of 'Vacation' class
            const response = await axios.get<Vacation[]>('http://localhost:3001/vacations/all_vacations');

            // Here we are receiving all the vacations from the server, as a JSON
            const allVacations : Vacation[] = response.data;

            store.dispatch({
                type: ActionType.GetAllVacations,
                payload: allVacations
            });
        }

        catch (error) {
            // alert(error.message);
            console.log(error);
        }
    }

    private updateUserNameValue = (event : ChangeEvent <HTMLInputElement>) => {

        // getting the username entered in the username's input field
        const userNameInputField : HTMLInputElement = event.target;
        const userNameInputFieldValue : string =  userNameInputField.value;
        const trimmedUserNameInputFieldValue : string = userNameInputFieldValue.trim();

        this.setState({
            userName: trimmedUserNameInputFieldValue
        });
    }

    private updatePasswordValue = (event : ChangeEvent <HTMLInputElement>) => {

        // getting the password entered in the password's input field
        const passwordInputField : HTMLInputElement = event.target;
        const passwordInputFieldValue : string =  passwordInputField.value;

        this.setState({
            password: passwordInputFieldValue
        });
    }

    private checkIfUsernameIsEmpty = () => {

        let userNameInputField: HTMLInputElement = document.getElementById("usernameField") as HTMLInputElement;
        let userNameInputFieldValue = userNameInputField.value;
        let trimmedUserNameInputFieldValue = userNameInputFieldValue.trim();

        if (trimmedUserNameInputFieldValue === "") {
            this.notifyUserAboutInvalidUserName();
            return false;
        }

        return true;
    }

    private checkIfPasswordIsEmpty = () => {

        let passwordInputField: HTMLInputElement = document.getElementById("passwordField") as HTMLInputElement;
        let passwordInputFieldValue = passwordInputField.value;
        let trimmedUserNameInputFieldValue = passwordInputFieldValue.trim();

        if (trimmedUserNameInputFieldValue === "") {
            this.notifyUserAboutInvalidPassword();
            return false;
        }

        return true;
    }

    private notifyUserAboutInvalidUserName = () => {
        this.usernameInputField.style.border = "2px solid red";
    }

    private notifyUserAboutInvalidPassword = () => {
        this.passwordInputField.style.border = "2px solid red";
    }

    private onIncorrectCredentials = () => {

        this.userMessagesParagraph.textContent = "Username Or Password Are Incorrect. Please Try Again";
    }

    private resetInputFields = () => {

        this.usernameInputField.style.border = "1px solid #00d1ff";
        this.passwordInputField.style.border = "1px solid #00d1ff";

        this.userMessagesParagraph.textContent = "";
    }

    private login = async () => {

        this.resetInputFields();

        let userNameIsNotEmpty = this.checkIfUsernameIsEmpty();
        let passwordIsNotEmpty = this.checkIfPasswordIsEmpty();

        if (userNameIsNotEmpty && passwordIsNotEmpty) {

            try {
                let userLoginDetails = new UserLoginDetails(this.state.userName, this.state.password);
                
                // The response we get, should be type of 'SuccessfulLoginServerResponse' class (token & user type)
                const response = await axios.post<SuccessfulLoginServerResponse>('http://localhost:3001/users/login', userLoginDetails);
    
                // Here we are receiving the token & user type from the data we got back from the server
                const serverResponseData = response.data;
    
                // Attaching the Token we received from the server, to every HTTP request we'll send from now on
                // This is our 'Interceptor'
                let brearerToken = "Bearer " + serverResponseData.token;
                axios.defaults.headers.common['Authorization'] = brearerToken;
    
                // A Function that handles the URL and SessionStorage based on the data received from the server
                this.handleUserInfoFromServer(serverResponseData);
            }
    
            catch (error) {

                this.onIncorrectCredentials();
            }
        }
    }

    private handleUserInfoFromServer = (serverResponseData : SuccessfulLoginServerResponse) => {

        // ---------- Inserting the user's info (Token & Type) to the sessionStorage ---------- //

        let strUserInfo = JSON.stringify(serverResponseData);
        sessionStorage.setItem('userInfo', strUserInfo);

        
        // ---------- Navigating To The Correct URL, Based On The User's Type ---------- //

        if (serverResponseData.userType === "ADMIN") {
            this.props.history.push('/home');
        }

        else if (serverResponseData.userType === "USER") {
            this.props.history.push('/user');
        }
    }



    render() {
        return (
            <div className="loginSection">

                <div className="loginSquare">
                    <img className="explorer-logo margin-bottom-1" src={logo} alt="Explorer"/>

                    <h2 className="sharp-text">Login</h2>
                    <h3 className="sharp-text margin-bottom-2">To Continue To Explorer</h3>


                    <div className="inputFieldSection">
                        <fieldset id="usernameFieldset" className="cerdinals-fieldset">
                            <legend>Username</legend>
                            <input onChange={this.updateUserNameValue} type="text" name="username" id="usernameField"/>
                        </fieldset>

                        <fieldset id="passwordFieldset" className="cerdinals-fieldset">
                            <legend>Password</legend>
                            <input onChange={this.updatePasswordValue} type="password" name="password" id="passwordField"/>
                        </fieldset>

                        <button className="loginBtn" onClick={this.login}>Login</button>
                    </div>

                    <NavLink className="notRegisteredLink sharp-text" to="/register">I'm Not Registered</NavLink>
                </div>

                <p id="userMessagesParagraph" className="userMessagesParagraph sharp-text"></p>

            </div>
        )
    }
}
