import React from 'react';
import favoriteBookmarkSVG from '../../assets/favoriteBookmarkSVG.svg';
import './topNavbar.css';

export default function TopNavbar() {

    window.onscroll = () => {

        if (document.body.scrollTop > 120 || document.documentElement.scrollTop > 120) {

            document.getElementById("topNavbar").style.backgroundColor = "#15304c";
            document.getElementById("topNavbar").style.boxShadow = "0 5px 3px #35353570";
          }

          else {

            document.getElementById("topNavbar").style.backgroundColor = "transparent";
            document.getElementById("topNavbar").style.boxShadow = "none";
          }
    }
    
    

    return (
        <div id="topNavbar" className="topNavbar">
            <div className="topNavItem">
                <h3 className="sharp-text">All Vacations</h3>
                <img className="topNavIcon sharp-text" src={favoriteBookmarkSVG} alt="Favorite Bookmark"/>
            </div>

            <div className="topNavItem">
                <h3 className="sharp-text">Favorite Vacations</h3>
                <img className="topNavIcon sharp-text" src={favoriteBookmarkSVG} alt="Favorite Bookmark"/>
            </div>
        </div>
    )
}
