import React, { Component } from 'react';
import heartGIF from '../../assets/poundingHeart.svg';
import FullHeart from '../../assets/fullHeart.svg';

import Vacation from '../../models/Vacation';
import './favoriteVacations.css';



interface vacationsState {
    favoriteVacations : Vacation[];
}


export default class FavoriteVacations extends Component <any, vacationsState> {

    private favoriteVacations : Vacation[] = new Array <Vacation> ();

    constructor(props : any) {
        super(props);

        const vacation1 = new Vacation("Hotel Andrea", 355, "01/01/2021", "07/01/2021", "./favoriteVacationsImages/hotelsImage1.jpg", 0);
        const vacation2 = new Vacation("Torpedo Hostel", 145, "03/01/2021", "06/01/2021", "./favoriteVacationsImages/hotelsImage2.jpg", 0);
        const vacation3 = new Vacation("Roi de Sicile Rivoli", 304, "21/01/2021", "12/02/2021", "./favoriteVacationsImages/hotelsImage3.jpg", 0);
        const vacation4 = new Vacation("Grand Priest Shaman", 250, "03/01/2021", "09/01/2021", "./favoriteVacationsImages/hotelsImage4.jpg", 0);
        const vacation5 = new Vacation("Torpedo Hostel", 100, "03/01/2021", "09/01/2021", "./favoriteVacationsImages/hotelsImage1.jpg", 0);
        const vacation6 = new Vacation("Grand Priest Shaman", 520, "03/01/2021", "09/01/2021", "./favoriteVacationsImages/hotelsImage3.jpg", 0);
        // const vacation5 = new Vacation("Harta For Mocking", 18, "22/02/2021", "30/02/2021", "./favoriteVacationsImages/hotelsImage5.jpg");


        this.favoriteVacations.push(vacation1);
        this.favoriteVacations.push(vacation2);
        this.favoriteVacations.push(vacation3);
        this.favoriteVacations.push(vacation4);
        this.favoriteVacations.push(vacation5);
        this.favoriteVacations.push(vacation6);
    

        this.state = {
            //  THE DEFAULT STARTING VALUES SHOULD BE FROM REDUX (from the store) (If Redux Is Necessary)
            favoriteVacations : this.favoriteVacations
        }
    }
    

    render() {
        return (
            <div className="favoriteVacationsSection">

                {/* ---------- Displayed Vacations Section ---------- */}

                <div className="vacationsDisplaySection">

                    {/* ---------- Favorite Vacations Section ---------- */}

                    <div className="favoriteVacationsSection">
                        <h3 className="favoritesHeader sharp-text">Your {this.state.favoriteVacations.length} Favorite Vacations <img className="heartGIF" src={heartGIF} alt="Followed GIF"/></h3>

                        <div className="favoriteVacations">

                            {/* ---------- Favorite Vacations Card ---------- */}
                        
                            {this.state.favoriteVacations.map( (vacation : Vacation, index : number) => 
                                <div className="vacationCardContainer" key={index}>
                                    <img className="vacationCardImg" src={require(`${vacation.imgURL}`)} alt={vacation.description}/>
    
                                    <div className="vacationCard">
                                        <div className="topVacationCardSection">
                                            <img className="heartIcon" src={FullHeart} alt="Like Button"/>
                                            <p className="followersText">{vacation.followersNumber}</p>
                                        </div>
    
                                        <div className="vacationInfoCard">
                                            <h4 className="resortName">{vacation.description}</h4>
    
                                            <div className="vacationDates">
                                                <h4 className="vacationDate">{vacation.startDate}</h4>
                                                <span> - </span>
                                                <h4 className="vacationDate">{vacation.endDate}</h4>
                                            </div>
    
                                            <h4 className="vacationPrice">{vacation.price} $</h4>
                                        </div>
                                    </div>
                                    
                                </div>
                            )}
                        </div>
                    </div>

                </div>

            </div>
        )
    }
}
