        let clickedVacationID = clickedVacation.vacationID;
        await axios.post(`http://localhost:3001/vacations/unfollow_vacation/${clickedVacationID}`);
        let indexOfClickedVacationInGlobalStore = this.getIndexOfFavoriteVacationByID(clickedVacationID); // Updating the Global Store. Sending the index of the vacation to the Reducer, in order to remove it from the Global Store's favorites

