import React, { ChangeEvent, Component } from 'react';
import logo from '../../assets/Explorer-logo.png';
import UserLoginDetails from '../../models/UserLoginDetails';
import SuccessfulLoginServerResponse from '../../models/SuccessfulLoginServerResponse';
import { NavLink } from 'react-router-dom';
import axios from 'axios';
import './loginPage.css';


interface LoginState {
    userName: string;
    password: string;
}

export default class LoginPage extends Component <any, LoginState> {

    public constructor (props: any) {
        super(props);

        // Retrieving all vacations from the DB before the user even logs in, in order to load
        // the data behind the scenes, while the user is bussy logging
        this.getAllVacationsFromServer();

    
        // initializing the state with empty values
        this.state = {
             userName: "",
             password: ""
        };
    }



    private getAllVacationsFromServer = async () => {
        
        
    }

    private updateUserNameValue = (event : ChangeEvent <HTMLInputElement>) => {

        // getting the username entered in the username's input field
        const userNameInputField : HTMLInputElement = event.target;
        const userNameInputFieldValue : string =  userNameInputField.value;
        const trimmedUserNameInputFieldValue : string = userNameInputFieldValue.trim();

        this.setState({
            userName: trimmedUserNameInputFieldValue
        });
    }

    private updatePasswordValue = (event : ChangeEvent <HTMLInputElement>) => {

        // getting the password entered in the password's input field
        const passwordInputField : HTMLInputElement = event.target;
        const passwordInputFieldValue : string =  passwordInputField.value;

        this.setState({
            password: passwordInputFieldValue
        });
    }


    private login = async () => {

        try {
            let userLoginDetails = new UserLoginDetails(this.state.userName, this.state.password);
            
            // The response we get, should be type of 'SuccessfulLoginServerResponse' class (token & user type)
            const response = await axios.post<SuccessfulLoginServerResponse>('http://localhost:3001/users/login', userLoginDetails);

            // Here we are receiving the token & user type from the data we got back from the server
            const serverResponseData = response.data;

            console.log(serverResponseData);
            

            // Attaching the Token we received from the server, to every HTTP request we'll send from now on
            // This is our 'Interceptor'
            axios.defaults.headers.common['Authorization'] = serverResponseData.token;

            this.handleNavigationAccordingToUserType(serverResponseData);
        }

        catch (error) {
            alert(error.message);
            console.log(error);
        }
    }

    private handleNavigationAccordingToUserType = (serverResponseData : SuccessfulLoginServerResponse) => {

        if (serverResponseData.userType === "ADMIN") {
            this.props.history.push('/admin');
            sessionStorage.setItem('userType', 'ADMIN');
        }

        else {
            this.props.history.push('/user');
            sessionStorage.setItem("userType", "USER");
        }
    }

    render() {
        return (
            <div className="loginSection">

                <div className="loginSquare">
                    <img className="explorer-logo margin-bottom-1" src={logo} alt="Explorer"/>

                    <h2 className="sharp-text">Login</h2>
                    <h3 className="sharp-text margin-bottom-2">To Continue To Explorer</h3>


                    <div className="inputFieldSection">
                        <fieldset className="cerdinals-fieldset">
                            <legend>Username</legend>
                            <input onChange={this.updateUserNameValue} type="text" name="username" id="usernameField"/>
                        </fieldset>

                        <fieldset className="cerdinals-fieldset">
                            <legend>Password</legend>
                            <input onChange={this.updatePasswordValue} type="password" name="password" id="passwordField"/>
                        </fieldset>

                        <button className="loginBtn" onClick={this.login}>Login</button>
                    </div>

                    <NavLink className="notRegisteredLink sharp-text" to="/register">I'm Not Registered</NavLink>
                </div>

            </div>
        )
    }
}
