import AppState from "./AppState";
import Action from "./Action";
import { ActionType } from "./ActionType";


export default function reduce (oldAppState : AppState, action : Action): AppState {

    const newAppState = {...oldAppState};

    switch (action.type) {
        case ActionType.GetAllVacations:
            newAppState.allVacations = [...action.payload];
            break;
        
        case ActionType.GetFavoriteVacations:
            newAppState.favoriteVacations.push(action.payload);
            break;

        case ActionType.RemoveVacationFromFavorites:
            let indexOfVacationToRemoveFromFavorites : number = newAppState.favoriteVacations.indexOf(action.payload);
            newAppState.favoriteVacations.splice(indexOfVacationToRemoveFromFavorites, 1);
            break;
    }

    return newAppState;
}
