import React, { Component } from 'react'
import { BrowserRouter, Route, Switch, Redirect } from 'react-router-dom';
import { getUserTypeFromServerCache } from '../../models/GetUserTypeFromServerCache';
import { isUserLoggedValidator } from '../../models/IsUserLoggedValidator';
import LoginPage from '../loginPage/LoginPage';
import RegisterPage from '../registerPage/RegisterPage';
import RerportsPage from '../reportsPage/ReportsPage';
import VacationsPage from '../vacationsPage/VacationsPage';
import './layout.css';


interface userTypeState {
    userTypeFromServer: string;
}

export default class Layout extends Component <any, userTypeState> {

    constructor(props: any) {
        super(props);

        this.state = {
            userTypeFromServer: ""
        };
    }
    
    componentDidMount = async () => {

        const isUserLogged = isUserLoggedValidator();

        if (isUserLogged) {

            const userTypeFromServer = await getUserTypeFromServerCache();

            this.setState({
                userTypeFromServer: userTypeFromServer
            });
        }
        alert(this.state.userTypeFromServer)
    }


    render() {
        return (
            <div className="layout" id="topPage">
                <BrowserRouter>
        
                    <Switch>
                        <Route path="/login" component={LoginPage} exact />
                        <Route path="/register" component={RegisterPage} exact />
                        <Route path="/vacations" component={VacationsPage} userTypeFromServerCache = {this.state.userTypeFromServer} exact />
                        <Route path="/reports" component={RerportsPage} exact />
                        
                        <Redirect from="/" to="/login" exact />
                    </Switch>
        
                </BrowserRouter>
            </div>
        )
    }
}
