import React, { Component } from 'react';
import LocationImage from '../../assets/locationImage.png';
import Vacation from '../../models/Vacation';
import './allVacations.css';


interface allVacationsState {
    allVacations : Vacation[];
}



export default class AllVacations extends Component <any, allVacationsState> {

    private allVacations = new Array <Vacation> ();

    constructor(props : any) {
        super(props);

        const vacation1 = new Vacation("Hotel Andrea", 355, "01/01/2021", "07/01/2021", "./favorteVacationsImages/hotelsImage1.jpg");
        const vacation2 = new Vacation("Torpedo Hostel", 145, "03/01/2021", "06/01/2021", "./favorteVacationsImages/hotelsImage2.jpg");
        const vacation3 = new Vacation("Roi de Sicile Rivoli", 304, "21/01/2021", "12/02/2021", "./favorteVacationsImages/hotelsImage3.jpg");
        const vacation4 = new Vacation("Grand Priest Shaman", 250, "03/01/2021", "09/01/2021", "./favorteVacationsImages/hotelsImage4.jpg");
        // const vacation5 = new Vacation("Harta For Mocking", 18, "22/02/2021", "30/02/2021", "./favorteVacationsImages/hotelsImage5.jpg");


        this.allVacations.push(vacation1);
        this.allVacations.push(vacation2);
        this.allVacations.push(vacation3);
        this.allVacations.push(vacation4);
        // this.allVacations.push(vacation5);
    
        this.state = {
            allVacations : this.allVacations
        }
    }
    

    render() {
        return (
            <div className="allVacationsSection">

                {/* ---------- All Vacations Section Header ---------- */}

                <h3 className="allVacationsHeader sharp-text">All Vacations
                    <img className="locationImage" src={LocationImage} alt="All Vacations"/>
                </h3>
            
                <div className="allVacations">

                </div>
            </div>
        )
    }
}
