import React, { Component, useEffect } from 'react';
import AllVacations from '../allVacations/AllVacations';
import FavoriteVacations from '../favoriteVacations/FavoriteVacations';
import TopNavbar from '../topNavbar/TopNavbar';
// import SpinningEarth from '../spinningEarthAnimation/SpinningEarth';
import { store } from '../../Redux/Store';
import './vacationsPage.css';
import { ActionType } from '../../Redux/ActionType';


export default function VacationsPage() {

    // Whenever the component is rendering
    useEffect( ()=> {

        updatingUserLoginStatusToGlobalStore();
    });


    const updatingUserLoginStatusToGlobalStore = () => {

        let userData = JSON.parse(sessionStorage.getItem("userInfo"));

        // Checking if the user has a correct sessionStorage JSON
        if (userData !== null) {

            // Checking if the user has a token
            if (userData.token !== undefined) {
                store.dispatch({
                    type: ActionType.SetUserLoginStatus
                });
            }
        }
    }

    return (
        <div className="vacationsAndNavbar">
            <div className="topNavbarSection">
                <TopNavbar />
            </div>

            <div className="allVacationsSectionsContainer">
                {/* <SpinningEarth /> */}
                <FavoriteVacations />
                <AllVacations />
            </div>
        </div>

    )
}
