import React, { Component } from 'react';
import LocationImage from '../../assets/locationImage.png';
import Vacation from '../../models/Vacation';
import EmptyHeart from '../../assets/likeImg.png';
import { store } from '../../Redux/Store';
import axios from 'axios';
import { ActionType } from '../../Redux/ActionType';
import { Unsubscribe } from 'redux';
import colorfulAstroid from '../../assets/wavyLine.png';
import {withRouter} from 'react-router';
import './allVacations.css';


interface allVacationsState {
    allVacations : Vacation[];
}

export class AllVacations extends Component <any, allVacationsState> {

    private unSubscribeStore : Unsubscribe;

    constructor(props : any) {
        super(props);

        this.state = {
            allVacations : new Array <Vacation> ()
        }
    }


    componentWillUnmount = () => {

        // Unsubscribing the store's listener right before the component will unmount
        // In order to make a small space optimization
        this.unSubscribeStore();
    }


    componentDidMount = () => {

        // Optimizing a bit the memory leak of the store listener
        // Also subscribes automatically to the store
        this.unSubscribeStore = store.subscribe(
            () => this.setState ({
                allVacations: store.getState().allVacations
            })
        );

        if (store.getState().allVacations.length === 0) {
            this.getAllVacationsFromServer();
        }

        else {
            this.setState ({
                allVacations: store.getState().allVacations
            });
        }
    }


    private checkIfUserIsLogged = () => {

        let userData = JSON.parse(sessionStorage.getItem("userInfo"));

        // Checking if the user has a correct sessionStorage JSON
        if (userData !== null) {

            // Checking if the user has a token
            if (userData.token !== undefined) {
                return true;
            }
        }

        return false;
    }

    private validateVacationIsNotAlreadyLikedByTheUser = (clickedVacation : Vacation) => {

        let favoriteVacationsFromGlobalState: Vacation[] = store.getState().favoriteVacations;
        let clickedVacationID: number = clickedVacation.Vacation_ID;

        // Searching the clicked vacation's ID inside our global store

        let clickedVacationInGlobalStore: Vacation = favoriteVacationsFromGlobalState.find( favoriteVacation => favoriteVacation.Vacation_ID === clickedVacationID);
        let indexOfClickedVacationInGlobalStore = clickedVacationInGlobalStore.Vacation_ID;

        // If no result came back (if the user hasn't liked this vacation already)
        if (indexOfClickedVacationInGlobalStore === undefined) {
            return true;
        }

        return false;
    }

    private getAllVacationsFromServer = async () => {
        
        try {
            // The response we get, should be type of 'Vacation' class
            const response = await axios.get<Vacation[]>('http://localhost:3001/vacations/all_vacations');

            // Here we are receiving all the vacations from the server, as a JSON
            const allVacations : Vacation[] = response.data;

            store.dispatch({
                type: ActionType.GetAllVacations,
                payload: allVacations
            });
        }

        catch (error) {
            // alert(error.message);
            console.log(error);
        }
    }

    private onVacationFollowClick = async (clickedVacation : Vacation) => {

        // Checking if the user is logged based on the global store
        const isUserLogged = this.checkIfUserIsLogged();

        // checking if the user hasn't liked this vacation already
        const vacationIsNotAlreadyLikedByTheUser = this.validateVacationIsNotAlreadyLikedByTheUser(clickedVacation);

        // If the user is currently logged in (If the current person is not a guest)
        if (isUserLogged) {

            // If the vacation selected is not already liked by the user
            if (vacationIsNotAlreadyLikedByTheUser) {
                
                try {

                    let clickedVacationID : number = clickedVacation.Vacation_ID;
                    await axios.post('http://localhost:3001/vacations/vacation_follow', {clickedVacationID});
    
                    // Update The Global Store

                    store.dispatch({type: ActionType.SetFollowedVacation, payload: clickedVacation});
                }
        
                catch (error) {
                    console.log(error);
                }
            }
        }

        else {

            // In case a guest tried to follow a vacation, send him to the login page in order for him to login (a guest is not logged in)
            this.props.history.push('/login');
        }
    }
    

    render() {
        return (
            <div className="allVacationsSection" id="allVacationsSection">

                {/* ---------- All Vacations Section Header ---------- */}

                <div className="headerContainer">
                    <h3 className="allVacationsHeader sharp-text">All {this.state.allVacations.length} Vacations We've Got For You
                        <img className="locationImage" src={LocationImage} alt="All Vacations"/>
                    </h3>

                    <img className="colorfulAstroidImg" src={colorfulAstroid} alt="Colorful Astroid"/>
                </div>
            

                <div className="allVacations">

                    {/* ---------- All Vacations Cards ---------- */}

                    {this.state.allVacations.map( (vacation : Vacation, index : number) => 
                        <div className="vacationCardContainer" key={index}>
                            <img className="vacationCardImg" src={vacation.Image_URL} alt={vacation.Vacation_Description}/>

                            <div className="vacationCard">
                                <div className="topVacationCardSection">
                                    <img className="heartIcon jackInTheBoxC" src={EmptyHeart} onClick={() => {this.onVacationFollowClick(vacation)}} alt="Like Button"/>
                                    <p className="followersText">{vacation.Followers_Count}</p>
                                </div>

                                <div className="vacationInfoCard">
                                    <h4 className="resortName">{vacation.Vacation_Description}</h4>

                                    <div className="vacationDates">
                                        <h4 className="vacationDate">{vacation.Start_Date}</h4>
                                        <span> - </span>
                                        <h4 className="vacationDate">{vacation.End_Date}</h4>
                                    </div>

                                    <h4 className="vacationPrice">{vacation.Vacation_Price} $</h4>
                                </div>
                            </div>
                            
                        </div>
                    )}

                </div>
            </div>
        )
    }
}

export default withRouter(AllVacations);
