import React from 'react';
import favoriteBookmarkSVG from '../../assets/favoriteBookmarkSVG.svg';
import coconutTreesSVG from '../../assets/coconutTreesSVG.svg';
import { useHistory } from "react-router-dom";
import axios from 'axios';
import './topNavbar.css';
import { store } from '../../Redux/Store';

export default function TopNavbar() {

    const history = useHistory();

    // Handles The User's Request To Logout And Login
    const onUserLoginStautsChange = () => {
        
        sessionStorage.clear();
        history.push("/login");
        window.location.reload();
    }

    const checkIfUserIsLogged = () => {

        if (store.getState().isUserLogged) {
            return true;
        }

        return false;
    }


    return (
        <div id="topNavbar" className="topNavbar">
            <a href="#allVacationsSection" className="topNavItem sharp-text">
                All Vacations
                <img className="topNavIcon sharp-text" src={coconutTreesSVG} alt="Favorite Bookmark"/>
            </a>

            <a href="#topPage" className="topNavItem sharp-text">
                Favorite Vacations
                <img className="topNavIcon sharp-text" src={favoriteBookmarkSVG} alt="Favorite Bookmark"/>
            </a>

            {checkIfUserIsLogged() ? <a onClick={onUserLoginStautsChange} className="topNavItem logoutButton">Log Out</a>
                                   : <a onClick={onUserLoginStautsChange} className="topNavItem loginButton">Log In</a>}

        </div>
    )
}
