import React, { Component } from 'react';
import Vacation from '../../models/Vacation';
import bigLogo from '../../assets/Explorer-logo-big.png';
import heartGIF from '../../assets/heartGIF.gif';
import emptyHeart from '../../assets/emptyHeart.svg';
import fullHeart from '../../assets/fullHeart.svg';

import './vacationsPage.css';


interface vacationsState {
    allVacations : Vacation[];
    followedVacations : Vacation[];
}

export default class VacationsPage extends Component <any, vacationsState> {

    constructor(props : any) {
        super(props);
    
        this.state = {
            //  THE DEFAULT STARTING VALUES SHOULD BE FROM REDUX (from the store)
             allVacations : new Array <Vacation> (),
             followedVacations : new Array <Vacation> ()
        }
    }
    

    render() {
        return (
            <div className="vacationsSection">

                <div className="vacationsSectionHeader">
                    <img className="bigLogo" src={bigLogo} alt="Explorer" />
                    <h2 className="sharp-text">Simply The Best Place To Order Your Next Vacation</h2>
                </div>


                <div className="vacationsDisplaySection">

                    <div className="favoriteVacationsSection">
                        <h2 className="favoritesHeader sharp-text">Favorites <img className="heartGIF" src={heartGIF} alt="Followed GIF"/></h2>


                        <div className="favoriteVacations">

                            <div className="vacationCardContainer">
                                <img className="vacationCardImg" src="https://pix10.agoda.net/hotelImages/5668227/0/7542736b26b0676a0e9e3c4aab831241.jpg?s=1024x768" alt=""/>

                                <div className="vacationCard">

                                    <div className="topVacationCardSection">
                                        <img className="heartIcon" src={emptyHeart} alt=""/>
                                    </div>

                                    <div className="vacationInfoCard">
                                        <h3 className="resortName">Hotel Andrea</h3>
                                        <h4 className="vacationPrice">355$</h4>
                                        <div className="vacationDates">
                                            <h4 className="sharp-text vacationDate">01/01/2021</h4>
                                            <span> - </span>
                                            <h4 className="sharp-text vacationDate">07/01/2021</h4>
                                        </div>
                                    </div>
        
                                </div>

                            </div>

                        </div>
                    </div>

                </div>

            </div>
        )
    }
}
