import React from 'react';
import favoriteBookmarkSVG from '../../assets/favoriteBookmarkSVG.svg';
import coconutTreesSVG from '../../assets/coconutTreesSVG.svg';
import { useHistory } from "react-router-dom";
import logoutIMG from '../../assets/logout.png';
import './topNavbar.css';
import { isUserLoggedValidator } from '../../models/IsUserLoggedValidator';

export default function TopNavbar() {

    const history = useHistory();

    // Handles The User's Request To Logout And Login
    const onUserLoginStautsChange = () => {
        
        sessionStorage.clear();
        history.push("/login");

        // reloading the page in order to delete the Global Store
        window.location.reload();
    }


    return (
        <div id="topNavbar" className="topNavbar">

            <a href="#topPage" className="topNavItem sharp-text">
                Favorite Vacations
            </a>

            {isUserLoggedValidator() ? <a onClick={onUserLoginStautsChange} className="topNavItem logoutButton">
                                        <img className="topNavIcon logoutIcon" src={logoutIMG} alt=""/>
                                     </a>
                                   :
                                     <a onClick={onUserLoginStautsChange} className="topNavItem loginButton">
                                        <img className="topNavIcon loginIcon" src={logoutIMG} alt=""/>
                                     </a>}

        </div>
    )
}
