import React, { ChangeEvent, Component } from 'react';
import { NavLink } from 'react-router-dom';
import logo from '../../assets/astronautSVG.svg';
import './registerPage.css';


interface UserRegisterState {
    firstName: string;
    lastName: string;
    userName: string;
    password: string;
}

export class RegisterPage extends Component <any, UserRegisterState> {

    constructor(props: any) {
        super(props);
    
        this.state = {
            firstName: "",
            lastName: "",
            userName: "",
            password: ""
        };
    }
    

    private onFirstNameInputValueChange = (event : ChangeEvent <HTMLInputElement>) => {

        // getting the username entered in the user's first name
        const firstNameInputField : HTMLInputElement = event.target;
        const userNameInputFieldValue : string =  userNameInputField.value;
        const trimmedUserNameInputFieldValue : string = userNameInputFieldValue.trim();

        this.setState({
            userName: trimmedUserNameInputFieldValue
        });
    }

    private onUserRegisterClick = () => {

    }

    
    render() {
        return (
            <div className="registerSection">

                <div className="registerSquare">
                    <img className="register-logo" src={logo} alt="Explorer"/>

                    <h2 className="sharp-text">Register</h2>
                    <h3 className="sharp-text margin-bottom-2">To Explorer</h3>


                    <div className="inputFieldSection">
                        <fieldset className="cerdinals-fieldset">
                            <legend>First Name</legend>
                            <input onChange={this.onFirstNameInputValueChange} type="text" name="firstName" id="firstNameField"/>
                        </fieldset>

                        <fieldset className="cerdinals-fieldset">
                            <legend>Last Name</legend>
                            <input type="text" name="lastName" id="lastNameField"/>
                        </fieldset>

                        <fieldset className="cerdinals-fieldset">
                            <legend>Username</legend>
                            <input type="text" name="username" id="usernameField"/>
                        </fieldset>

                        <fieldset className="cerdinals-fieldset">
                            <legend>Password</legend>
                            <input type="password" name="password" id="passwordField"/>
                        </fieldset>

                        <button className="registerBtn" onClick={this.onUserRegisterClick}>Register</button>
                    </div>

                    <NavLink className="alreadyRegisteredLink sharp-text" to="/login">I'm Already Registered</NavLink>
                </div>

                <p id="userMessagesParagraph" className="userMessagesParagraph sharp-text"></p>
            </div>
        )
    }
}

export default RegisterPage
