import React, { Component } from 'react';
import poundingHeartAnimation from '../../assets/poundingHeart.svg';
import fullHeart from '../../assets/likedIMG.png';
import axios from 'axios';
import Vacation from '../../models/Vacation';
import { store } from '../../Redux/Store';
import { Unsubscribe } from 'redux';
import { ActionType } from '../../Redux/ActionType';
import colorfulAstroid from '../../assets/wavyLine.png';
import noVacations from '../../assets/noVacations.png';
import {withRouter} from 'react-router';
import './favoriteVacations.css';
import { isUserLoggedValidator } from '../../models/IsUserLoggedValidator';


interface VacationsState {
    favoriteVacations : Vacation[];
}

export class FavoriteVacations extends Component <any, VacationsState> {
    
    private unSubscribeStore : Unsubscribe;

    constructor(props : any) {
        super(props);

        this.state = {
            favoriteVacations : new Array <Vacation> ()
        }
    }

    componentWillUnmount = () => {

        // Unsubscribing the store's listener right before the component will unmount
        // In order to make a small space optimization
        this.unSubscribeStore();
    }

    componentDidMount = () => {

        // Explanation -> The interceptor is a just JS variable (from axios library). On refresh = it resets.
        // So, this function takes the token the user got from the server (which is now inside the sessionStorage),
        // and assign it to the interceptor
        this.handleInterceptor();


        // Optimizing a bit the memory leak of the store listener
        // Also subscribes automatically to the store
        this.unSubscribeStore = store.subscribe(
            () => this.setState ({
                favoriteVacations: store.getState().favoriteVacations
            })
        );

        // Checks whether the user's favorite vacations exist in the global store
        this.checkIfGlobalStoreHasFavoriteVacations();
    }



    private checkIfGlobalStoreHasFavoriteVacations = () => {

        // If our favorite vacations state in the global store is empty, fetch the favorite vacations from the server and insert them to the global store
        if (store.getState().favoriteVacations.length === 0) {
            this.getFavoriteVacationsFromServer();
        }

        // If we have the user's favorite vacations in the global store, fetch them instead of sending a request to the server
        else {
            this.setState ({
                favoriteVacations: store.getState().favoriteVacations
            });
        }
    }

    private handleInterceptor = () => {

        // Explanation -> The interceptor is a just JS variable (from axios library). On refresh = it resets.
        // So, this function takes the token the user got from the server (which is now inside the sessionStorage),
        // and assign it to the interceptor

        // Checking if the interceptor is empty
        if (axios.defaults.headers.common['Authorization'] === undefined) {

            let userData = JSON.parse(sessionStorage.getItem("userInfo"));

            // Checking if the user has a correct sessionStorage JSON
            if (userData !== null) {

                // Checking if the user has a token
                if (userData.token !== undefined) {

                    let brearerToken = "Bearer " + userData.token;
                    axios.defaults.headers.common['Authorization'] = brearerToken;
                }
            }
        }
    }

    private OnUserDislikeClick = (clickedVacation : Vacation) => {

        let clickedVacationID: number = clickedVacation.vacationID;

        this.removeClickedVacationFromFavorites(clickedVacationID);
    }

    private removeClickedVacationFromFavorites = async (clickedVacationID : number) => {

        /*
            Before the action, checking if the user is currently logged in.
            This validation happens because a user might delete his token before trying
            to like a vacation. In that case, throw him out to login again.
        */

        // Using a Model I made to check if the user is still logged (Preventing duplication of code from all component that needs to make this validation).
        // Checking if the user is logged based on the global store
        const isUserLogged = isUserLoggedValidator();

        if (isUserLogged) {
            try {
                // The response we get, should be type of 'Vacation' class
                await axios.post(`http://localhost:3001/vacations/unfollow_vacation/${clickedVacationID}`);
    
    
                // -------------------- Inserting the vacation to the global store for future use (acts like cache) -------------------- //
    
                let favoriteVacations: Vacation[] = store.getState().favoriteVacations as Vacation[];
    
                // Finding the clicked vacation in the global store, in order to remove it
                let clickedVacationInGlobalStore: Vacation = favoriteVacations.find( favoriteVacation =>  favoriteVacation.vacationID === clickedVacationID);
                let indexOfClickedVacationInGlobalStore: number = favoriteVacations.indexOf(clickedVacationInGlobalStore);
    
    
                // Updating the global store, in order to refresh the view without the unfollowed vacation
                store.dispatch({
                    type: ActionType.UnfolloweVacation,
                    payload: indexOfClickedVacationInGlobalStore
                });
            }
    
            catch (error) {
                let errorMessage = error.response.data.error;
                this.favoriteVacationsErrorHandler(errorMessage);
            }
        }

        else {
            // In case a guest tried to follow a vacation, send him to the login page in order for him to login (a guest is not logged in)
            this.props.history.push('/login');
        }
    }

    private getFavoriteVacationsFromServer = async () => {
        
        /*
            Before the action, checking if the user is currently logged in.
            This validation happens because a user might delete his token before trying
            to like a vacation. In that case, throw him out to login again.
        */

        // Using a Model I made to check if the user is still logged (Preventing duplication of code from all component that needs to make this validation).
        // Checking if the user is logged based on the global store
        const isUserLogged = isUserLoggedValidator();

        if (isUserLogged) {
            try {
                // The response we get, should be type of 'Vacation' class
                const response = await axios.get<Vacation[]>('http://localhost:3001/vacations/favorite_vacations');
                
                // Here we are receiving all the vacations from the server, as a JSON
                const favoriteVacations : Vacation[] = response.data;
    
                store.dispatch({
                    type: ActionType.GetFavoriteVacations,
                    payload: favoriteVacations
                });
            }
    
            catch (error) {

                try {
                    let errorMessage = error.response.data.error;
                    this.favoriteVacationsErrorHandler(errorMessage);
                }
                catch {
                    this.favoriteVacationsErrorHandler(error);
                }

            }
        }

    }

    private favoriteVacationsErrorHandler = (errorMessage: string) => {

        // Letting the user know he received an error
        let snackbar: HTMLDivElement = document.getElementById("snackbar") as HTMLDivElement;
        snackbar.className = "show";
        snackbar.textContent = errorMessage;

        setTimeout(function(){ snackbar.className = snackbar.className.replace("show", ""); }, 3000);
    }


    render() {
        return (
            <div>
                {
                    this.state.favoriteVacations.length > 0
                    &&
                    
                        <div className="favoriteVacationsSection">

                            <div className="headerContainer">
                                <h2 className="sharp-text favoritesHeader">Your Favorite Vacations</h2>
                            </div>


                            <div className="favoriteVacations">

                                {/* ---------- Favorite Vacations Cards ---------- */}

                                {this.state.favoriteVacations.map( (vacation : Vacation, index : number) => 
                                    <div className="vacationCardContainer" key={index}>

                                        <div className="vacationCardWithoutInfoSection">
                                            <img className="vacationCardImg" src={vacation.imageURL} alt={vacation.vacationDescription}/>
                                
                                            <div className="vacationCard">
                                                <div className="topVacationCardSection">
                                                    <img className="dislike" onClick={ () => this.OnUserDislikeClick(vacation)} src={fullHeart} alt="Like Button"/>
                                                    <p className="followersText">{vacation.followersCount}</p>
                                                </div>
                                            </div>
                                        </div>

                                        <div className="vacationInfoCard">
                                            <h4 className="resortName">{vacation.vacationDescription}</h4>
                                
                                            <div className="vacationDates">
                                                <h4 className="vacationDate">{vacation.startDate}</h4>
                                                <span> - </span>
                                                <h4 className="vacationDate">{vacation.endDate}</h4>
                                            </div>
                                
                                            <h4 className="vacationPrice">{vacation.vacationPrice} $</h4>
                                        </div>

                                    </div>
                                )}
                            </div>
                        </div>
                }

                {/* The User's Snackbar Error Indication */}
                <div id="snackbar"></div>

            </div>
        )
    }
}

export default withRouter(FavoriteVacations);
