import React, { Component } from 'react';
import LocationImage from '../../assets/locationImage.png';
import Vacation from '../../models/Vacation';
import emptyHeart from '../../assets/likeImg.png';
import fullHeart from '../../assets/likedIMG.png';
import SpinningEarthAnimation from '../spinningEarthAnimation/SpinningEarth';
import { store } from '../../Redux/Store';
import axios from 'axios';
import { ActionType } from '../../Redux/ActionType';
import { Unsubscribe } from 'redux';
import colorfulAstroid from '../../assets/wavyLine.png';
import {withRouter} from 'react-router';
import './allVacations.css';
import settingsImage from '../../assets/settingsImg.png';
import { isUserLoggedValidator } from '../../models/IsUserLoggedValidator';


interface allVacationsState {
    allVacations : Vacation[];
}

export class AllVacations extends Component <any, allVacationsState> {

    private unSubscribeStore : Unsubscribe;

    constructor(props : any) {
        super(props);

        this.state = {
            allVacations : new Array <Vacation> ()
        }
    }


    componentWillUnmount = () => {

        // Unsubscribing the store's listener right before the component will unmount
        // In order to make a small space optimization
        this.unSubscribeStore();
    }


    componentDidMount = () => {

        // Optimizing a bit the memory leak of the store listener
        // Also subscribes automatically to the store
        this.unSubscribeStore = store.subscribe(
            () => this.setState ({
                allVacations: store.getState().allVacations
            })
        );

        if (store.getState().allVacations.length === 0) {
            this.getAllVacationsFromServer();
        }

        else {
            this.setState ({
                allVacations: store.getState().allVacations
            });
        }
    }



    private validateVacationIsNotAlreadyLikedByTheUser = (clickedVacation : Vacation) => {

        let favoriteVacationsFromGlobalState: Vacation[] = store.getState().favoriteVacations;
        let clickedVacationID: number = clickedVacation.Vacation_ID;

        // Searching the clicked vacation's ID inside our global store

        let clickedVacationInGlobalStore: Vacation = favoriteVacationsFromGlobalState.find( favoriteVacation => favoriteVacation.Vacation_ID === clickedVacationID);

        // If no result came back (if the user hasn't liked this vacation already)
        if (clickedVacationInGlobalStore === undefined) {
            return true;
        }

        return false;
    }

    private getAllVacationsFromServer = async () => {
        
        try {
            // The response we get, should be type of 'Vacation' class
            const response = await axios.get<Vacation[]>('http://localhost:3001/vacations');

            // Here we are receiving all the vacations from the server, as a JSON
            const allVacations : Vacation[] = response.data;

            store.dispatch({
                type: ActionType.GetAllVacations,
                payload: allVacations
            });
        }

        catch (error) {

            try {
                let errorMessage = error.response.data.error;
                this.vacationsErrorHandler(errorMessage);
            }

            catch {
                this.vacationsErrorHandler(error);
            }

        }
    }

    private followVacation = async (clickedVacation : Vacation) => {
        try {

            let clickedVacationID : number = clickedVacation.Vacation_ID;
            await axios.post('http://localhost:3001/vacations/follow_vacation', {clickedVacationID});

            // Update The Global Store
            store.dispatch({type: ActionType.SetFollowedVacation, payload: clickedVacation});
        }

        catch (error) {

            let errorMessage = error.response.data.error;
            this.vacationsErrorHandler(errorMessage);
        }
    }

    private unfollowVacation = async (clickedVacation : Vacation) => {
        try {

            let clickedVacationID : number = clickedVacation.Vacation_ID;
            await axios.post('http://localhost:3001/vacations/unfollow_vacation', {clickedVacationID});


            // -------------------- Removing the vacation from the global store -------------------- //

            let favoriteVacations: Vacation[] = store.getState().favoriteVacations as Vacation[];

            // Finding the clicked vacation in the global store, in order to remove it
            let clickedVacationInGlobalStore: Vacation = favoriteVacations.find( favoriteVacation =>  favoriteVacation.Vacation_ID === clickedVacationID);
            let indexOfClickedVacationInGlobalStore: number = favoriteVacations.indexOf(clickedVacationInGlobalStore);

            // Update The Global Store
            store.dispatch({type: ActionType.UnfolloweVacation, payload: indexOfClickedVacationInGlobalStore});
        }

        catch (error) {

            let errorMessage = error.response.data.error;
            this.vacationsErrorHandler(errorMessage);
        }
    }

    private vacationsErrorHandler = (errorMessage: string) => {

        // Letting the user know he received an error
        let snackbar: HTMLDivElement = document.getElementById("snackbar") as HTMLDivElement;
        snackbar.className = "show";
        snackbar.textContent = errorMessage;

        setTimeout(function(){ snackbar.className = snackbar.className.replace("show", ""); }, 3000);
    }

    private onVacationFollowAttempt = (clickedVacation : Vacation) => {

        /*
            Before the action, checking if the user is currently logged in.
            This validation happens because a user might delete his token before trying
            to like a vacation. In that case, throw him out to login again.
        */

        // Using a Model I made to check if the user is still logged (Preventing duplication of code from all component that needs to make this validation).
        // Checking if the user is logged based on the global store
        const isUserLogged = isUserLoggedValidator();

        // If the user is currently logged in (If the current person is not a guest)
        if (isUserLogged) {

            // checking if the user hasn't liked this vacation already
            const vacationIsNotAlreadyLikedByTheUser = this.validateVacationIsNotAlreadyLikedByTheUser(clickedVacation);

            // If the vacation selected is not already liked by the user, follow that vacation
            if (vacationIsNotAlreadyLikedByTheUser) {
                this.followVacation(clickedVacation);
            }

            // If the vacation selected is ALREADY liked by the user, unfollow that vacation
            else {
                this.unfollowVacation(clickedVacation);
            }
        }

        else {
            // In case a guest tried to follow a vacation, send him to the login page in order for him to login (a guest is not logged in)
            this.props.history.push('/login');
        }
    }

    private checkIfVacationIsFollowed = (vacation : Vacation) => {

        // Inserting the vacation to the global store for future use (acts like cache)
        let favoriteVacations: Vacation[] = store.getState().favoriteVacations as Vacation[];
        let clickedVacationID = vacation.Vacation_ID;

        // Finding the clicked vacation in the global store, in order to remove it
        let clickedVacationInGlobalStore: Vacation = favoriteVacations.find( favoriteVacation =>  favoriteVacation.Vacation_ID === clickedVacationID);

        if (clickedVacationInGlobalStore === undefined) {
            return false;
        }

        return true;
    }
    

    render() {
        return (
            <div>

                {
                    this.state.allVacations.length > 0 ?

                    <div className="allVacationsSection" id="allVacationsSection">

                        {/* ---------- All Vacations Section Header ---------- */}

                        <div className="headerContainer">
                            <h3 className="allVacationsHeader sharp-text">All {this.state.allVacations.length} Vacations We've Got For You
                            </h3>
                        </div>
                    

                        <div className="allVacations">

                            {/* ---------- All Vacations Cards ---------- */}

                            {this.state.allVacations.map( (vacation : Vacation, index : number) => 
                                <div className="vacationCardContainer" key={index}>

                                    <div className="vacationCardWithoutInfoSection">
                                        <img className="vacationCardImg" src={vacation.Image_URL} alt={vacation.Vacation_Description}/>

                                        <div className="vacationCard">
                                        
                                            <div className="topVacationCardSection">
                                                {this.checkIfVacationIsFollowed(vacation) ?
                                                    <img className="heartIcon dislike" src={fullHeart} onClick={() => {this.onVacationFollowAttempt(vacation)}} alt="Like Button"/>
                                                :
                                                    <img className="heartIcon" src={emptyHeart} onClick={() => {this.onVacationFollowAttempt(vacation)}} alt="Like Button"/>
                                                }
                                                <p className="followersText">{vacation.Followers_Count}</p>
                                            </div>
                                        </div>
                                    </div>
        
                                    <div className="vacationInfoCard">
                                        <h4 className="resortName">{vacation.Vacation_Description}</h4>

                                        <div className="vacationDates">
                                            <h4 className="vacationDate">{vacation.Start_Date}</h4>
                                            <span> - </span>
                                            <h4 className="vacationDate">{vacation.End_Date}</h4>
                                        </div>

                                            <h4 className="vacationPrice">{vacation.Vacation_Price} $</h4>
                                    </div>
                                    
                            </div>
                        )}

                    </div>


                        {/* The User's Snackbar Error Indication */}
                        <div id="snackbar"></div>
                    </div>

                    :

                    <div className="noVcationsSection">
                        <img className="settingsImage" src={settingsImage} alt=""/>
                        <h1 className="sharp-text">We Are Sorry, There Was a Problem</h1>
                        <h1 className="sharp-text">Getting The Vacations From The Server</h1>
                    </div>
                }

            </div>
        )
    }
}

export default withRouter(AllVacations);
