import React, { Component } from 'react';
import LocationImage from '../../assets/locationImage.png';
import Vacation from '../../models/Vacation';
import EmptyHeart from '../../assets/emptyHeart.svg';
import FullHeart from '../../assets/fullHeart.svg';
import './allVacations.css';


interface allVacationsState {
    allVacations : Vacation[];
}



export default class AllVacations extends Component <any, allVacationsState> {

    private allVacations = new Array <Vacation> ();

    constructor(props : any) {
        super(props);

        const vacation1 = new Vacation("Hotel Andrea", 355, "01/01/2021", "07/01/2021", "./allVacationsImages/hotelsImage1.jpg");
        const vacation2 = new Vacation("Torpedo Hostel", 145, "03/01/2021", "06/01/2021", "./allVacationsImages/hotelsImage2.jpg");
        const vacation3 = new Vacation("Roi de Sicile Rivoli", 304, "21/01/2021", "12/02/2021", "./allVacationsImages/hotelsImage3.jpg");
        const vacation4 = new Vacation("Grand Priest Shaman", 250, "03/01/2021", "09/01/2021", "./allVacationsImages/hotelsImage4.jpg");
        const vacation5 = new Vacation("Hotel Andrea", 355, "01/01/2021", "07/01/2021", "./allVacationsImages/hotelsImage1.jpg");
        const vacation6 = new Vacation("Torpedo Hostel", 145, "03/01/2021", "06/01/2021", "./allVacationsImages/hotelsImage2.jpg");
        const vacation7 = new Vacation("Roi de Sicile Rivoli", 304, "21/01/2021", "12/02/2021", "./allVacationsImages/hotelsImage3.jpg");
        const vacation8 = new Vacation("Grand Priest Shaman", 250, "03/01/2021", "09/01/2021", "./allVacationsImages/hotelsImage4.jpg");
        const vacation9 = new Vacation("Hotel Andrea", 355, "01/01/2021", "07/01/2021", "./allVacationsImages/hotelsImage1.jpg");
        const vacation10 = new Vacation("Torpedo Hostel", 145, "03/01/2021", "06/01/2021", "./allVacationsImages/hotelsImage2.jpg");
        const vacation11 = new Vacation("Roi de Sicile Rivoli", 304, "21/01/2021", "12/02/2021", "./allVacationsImages/hotelsImage3.jpg");
        // const vacation5 = new Vacation("Harta For Mocking", 18, "22/02/2021", "30/02/2021", "./allVacationsImages/hotelsImage5.jpg");


        this.allVacations.push(vacation1);
        this.allVacations.push(vacation2);
        this.allVacations.push(vacation3);
        this.allVacations.push(vacation4);
        this.allVacations.push(vacation5);
        this.allVacations.push(vacation6);
        this.allVacations.push(vacation7);
        this.allVacations.push(vacation8);
        this.allVacations.push(vacation9);
        this.allVacations.push(vacation10);
        this.allVacations.push(vacation11);
        // this.allVacations.push(vacation5);
    
        this.state = {
            allVacations : this.allVacations
        }
    }
    

    render() {
        return (
            <div className="allVacationsSection">

                {/* ---------- All Vacations Section Header ---------- */}

                <h3 className="allVacationsHeader sharp-text">All {this.state.allVacations.length} Vacations
                    <img className="locationImage" src={LocationImage} alt="All Vacations"/>
                </h3>
            
                <div className="allVacations">
                <div className="favoriteVacations">

                {/* ---------- Favorite Vacations Card ---------- */}

                {this.state.allVacations.map( (vacation : Vacation, index : number) => 
                    <div className="vacationCardContainer" key={index}>
                        <img className="vacationCardImg" src={require(`${vacation.imgURL}`)} alt={vacation.description}/>

                        <div className="vacationCard">
                            <div className="topVacationCardSection">
                                <img className="heartIcon" src={EmptyHeart} alt="Like Button"/>
                            </div>

                            <div className="vacationInfoCard">
                                <h4 className="resortName">{vacation.description}</h4>

                                <div className="vacationDates">
                                    <h4 className="vacationDate">{vacation.startDate}</h4>
                                    <span> - </span>
                                    <h4 className="vacationDate">{vacation.endDate}</h4>
                                </div>

                                <h4 className="vacationPrice">{vacation.price} $</h4>
                            </div>
                        </div>
                        
                    </div>
                )}
                </div>
                </div>
            </div>
        )
    }
}
