import React from 'react';
import favoriteBookmarkSVG from '../../assets/favoriteBookmarkSVG.svg';
import coconutTreesSVG from '../../assets/coconutTreesSVG.svg';
import { useHistory } from "react-router-dom";
import { store } from '../../Redux/Store';
import { ActionType } from '../../Redux/ActionType';
import './topNavbar.css';

export default function TopNavbar() {

    const history = useHistory();

    // Handles The User's Request To Logout
    const onUserLogout = () => {
        
        sessionStorage.clear();
        history.push("/login");
    }


    return (
        <div id="topNavbar" className="topNavbar">
            <a href="#allVacationsSection" className="topNavItem sharp-text">
                All Vacations
                <img className="topNavIcon sharp-text" src={coconutTreesSVG} alt="Favorite Bookmark"/>
            </a>

            <a href="#topPage" className="topNavItem sharp-text">
                Favorite Vacations
                <img className="topNavIcon sharp-text" src={favoriteBookmarkSVG} alt="Favorite Bookmark"/>
            </a>

            <a onClick={onUserLogout} className="topNavItem">
                {sessionStorage.getItem("isUserLogged") ? 'Logout' : 'Log In'}
            </a>
        </div>
    )
}
