import React, { Component } from 'react';
import poundingHeartAnimation from '../../assets/poundingHeart.svg';
import FullHeart from '../../assets/likeImg.png';
import axios from 'axios';
import Vacation from '../../models/Vacation';
import { store } from '../../Redux/Store';
import { Unsubscribe } from 'redux';
import { ActionType } from '../../Redux/ActionType';
import colorfulAstroid from '../../assets/wavyLine.png';
import noVacations from '../../assets/noVacations.png';
import './favoriteVacations.css';


interface VacationsState {
    favoriteVacations : Vacation[];
}

export default class FavoriteVacations extends Component <any, VacationsState> {
    
    private unSubscribeStore : Unsubscribe;

    constructor(props : any) {
        super(props);

        this.state = {
            favoriteVacations : new Array <Vacation> ()
        }
    }

    componentWillUnmount = () => {
        // Unsubscribing the store's listener right before the component will unmount
        // In order to make a small space optimization
        this.unSubscribeStore();
    }

    componentDidMount = () => {

        // Explanation -> The interceptor is a just JS variable (from axios library). On refresh = it resets.
        // So, this function takes the token the user got from the server (which is now inside the sessionStorage),
        // and assign it to the interceptor
        this.handleInterceptor();


        // Optimizing a bit the memory leak of the store listener
        // Also subscribes automatically to the store
        this.unSubscribeStore = store.subscribe(
            () => this.setState ({
                favoriteVacations: store.getState().favoriteVacations
            })
        );

        if (store.getState().favoriteVacations.length === 0) {
            this.getFavoriteVacationsFromServer();
        }

        else {
            this.setState ({
                favoriteVacations: store.getState().favoriteVacations
            });
        }
    }


    private handleInterceptor = () => {

        // Explanation -> The interceptor is a just JS variable (from axios library). On refresh = it resets.
        // So, this function takes the token the user got from the server (which is now inside the sessionStorage),
        // and assign it to the interceptor

        // Checking if the interceptor is empty
        if (axios.defaults.headers.common['Authorization'] === undefined) {

            let userData = JSON.parse(sessionStorage.getItem("userInfo"));

            // Checking if the user has a correct sessionStorage JSON
            if (userData !== null) {

                // Checking if the user has a token
                if (userData.token !== undefined) {

                    let brearerToken = "Bearer " + userData.token;
                    axios.defaults.headers.common['Authorization'] = brearerToken;
                }
            }
        }
    }

    private getFavoriteVacationsFromServer = async () => {
        
        try {
            // The response we get, should be type of 'Vacation' class
            const response = await axios.get<Vacation[]>('http://localhost:3001/vacations/favorite_vacations');
            
            // Here we are receiving all the vacations from the server, as a JSON
            const favoriteVacations : Vacation[] = response.data;
            console.log(favoriteVacations);
            

            store.dispatch({
                type: ActionType.GetFavoriteVacations,
                payload: favoriteVacations
            });
        }

        catch (error) {
            // alert(error.message);
            console.log(error);
        }
    }


    render() {
        return (
            <div className="favoriteVacationsSection">

                {/* ---------- Displayed Vacations Section ---------- */}

                <div className="vacationsDisplaySection">

                    {/* ---------- Favorite Vacations Section ---------- */}

                    <div className="favoriteVacationsSection">
                        <div className="headerContainer">
                            <h3 className="favoritesHeader sharp-text">Your {this.state.favoriteVacations.length} Favorite Vacations
                                <img className="poundingHeartAnimation" src={poundingHeartAnimation} alt="Followed GIF"/>
                            </h3>

                            <img className="colorfulAstroidImg" src={colorfulAstroid} alt="Colorful Astroid"/>
                        </div>


                        <div className="favoriteVacations">

                            {/* ---------- Favorite Vacations Cards ---------- */}

                            {/* Indicating the user that he does not have any favorite vacations */}
                            <div className="noFavoriteVacations">
                                {
                                    this.state.favoriteVacations.length === 0
                                    &&
                                    <img src={noVacations} alt="No Favorite Vacations Found"></img>
                                }
                            </div>
                            

                            {this.state.favoriteVacations.map( (vacation : Vacation, index : number) => 
                                <div className="vacationCardContainer" key={index}>
                                    <img className="vacationCardImg" src={vacation.Image_URL} alt={vacation.Vacation_Description}/>
    
                                    <div className="vacationCard">
                                        <div className="topVacationCardSection">
                                            <img className="dislike" src={FullHeart} alt="Like Button"/>
                                            <p className="followersText">{vacation.Followers_Count}</p>
                                        </div>
    
                                        <div className="vacationInfoCard">
                                            <h4 className="resortName">{vacation.Vacation_Description}</h4>
    
                                            <div className="vacationDates">
                                                <h4 className="vacationDate">{vacation.Start_Date}</h4>
                                                <span> - </span>
                                                <h4 className="vacationDate">{vacation.End_Date}</h4>
                                            </div>
    
                                            <h4 className="vacationPrice">{vacation.Vacation_Price} $</h4>
                                        </div>
                                    </div>
                                    
                                </div>
                            )}
                        </div>
                    </div>

                </div>

            </div>
        )
    }
}
