import React, { useEffect } from 'react';
import { useHistory } from "react-router-dom";
import { isUserLoggedValidator } from '../../models/IsUserLoggedValidator';
import { store } from '../../Redux/Store';
import { ActionType } from '../../Redux/ActionType';
import './topNavbar.css';


export default function TopNavbar() {

    const history = useHistory();

    useEffect( ()=> {
        window.removeEventListener("scroll", changeNavbarOnScroll);
    }, []);

    // Handles The User's Request To Logout And Login
    const onUserLoginStautschangeNavbarOnScroll = () => {
        
        sessionStorage.clear();
        history.push("/login");

        // Clearing the user's favorite vacations from the Global Store
        store.dispatch({
            type: ActionType.OnUserLogout
        })
    }

    const changeNavbarOnScroll = () => {
        if (document.body.scrollTop > 130 || document.documentElement.scrollTop > 130) {
            document.getElementById("topNavbar").style.backgroundColor = "#174d5f";
            document.getElementById("topNavbar").style.color = "white";
        }

        else if (document.body.scrollTop <= 130 || document.documentElement.scrollTop <= 130) {
            document.getElementById("topNavbar").style.backgroundColor = "transparent";
            document.getElementById("topNavbar").style.color = "#15304c";
        }
    }

    // When the user scrolls down, change the navigation bar
    window.addEventListener('scroll', changeNavbarOnScroll);


    return (
        <div id="topNavbar" className="topNavbar">

            {isUserLoggedValidator() ? 
                <div className="helloUserSection">
                    {
                        // Displaying The Username In The Navigation Bar
                        'Howdy, ' +
                        JSON.parse(sessionStorage.getItem("userInfo")).userName
                    }
                    <a onClick={onUserLoginStautschangeNavbarOnScroll} className="topNavItem loginStatusButton">
                        Logout
                    </a>
                </div>
                :
                <div className="helloUserSection">
                    Howdy, Guest
                    <a onClick={onUserLoginStautschangeNavbarOnScroll} className="topNavItem loginStatusButton">
                        Login
                    </a>
                </div>
            }

        </div>
    )
}
