import React from 'react';
import favoriteBookmarkSVG from '../../assets/favoriteBookmarkSVG.svg';
import coconutTreesSVG from '../../assets/coconutTreesSVG.svg';
import { useHistory } from "react-router-dom";
import axios from 'axios';
import './topNavbar.css';

export default function TopNavbar() {

    const history = useHistory();

    // Handles The User's Request To Logout
    const onUserLogout = () => {
        
        sessionStorage.clear();
        history.push("/login");
    }

    const checkIfUserIsLogged = () => {

        let userData = JSON.parse(sessionStorage.getItem("userInfo"));

        // Checking if the user has a correct sessionStorage JSON
        if (userData !== null) {

            // Checking if the user has a token
            if (userData.token !== undefined) {
                return true;
            }
        }

        return false;
    }


    return (
        <div id="topNavbar" className="topNavbar">
            <a href="#allVacationsSection" className="topNavItem sharp-text">
                All Vacations
                <img className="topNavIcon sharp-text" src={coconutTreesSVG} alt="Favorite Bookmark"/>
            </a>

            <a href="#topPage" className="topNavItem sharp-text">
                Favorite Vacations
                <img className="topNavIcon sharp-text" src={favoriteBookmarkSVG} alt="Favorite Bookmark"/>
            </a>

            {checkIfUserIsLogged() ? <a onClick={onUserLogout} className="topNavItem logoutButton">Log Out</a>
                                   : <a onClick={onUserLogout} className="topNavItem loginButton">Log In</a>}

        </div>
    )
}
